import { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components"
import userImg from "../icons/user.png"
import shoppingImg from "../icons/shopping.png"
import confirm from "../icons/confirm.png"


export default function MainPage() {
  const URL = "http://localhost:5000/products"
  const [products, setProducts] = useState([]);
  const [selected, setSelected] = useState([]);
  useEffect(() => {
    axios.get(URL)
    .then((response) => {
      setProducts(response.data);
    })
    .catch((err) => {console.log(err);})
  }, []);

  console.log(products)

  function selectedIten(id){
    const aux = [...selected];
    if(aux.includes(id)){
      aux.splice(aux.indexOf(id), 1);
      setSelected(aux);
    } else {
      aux.push(id)
      setSelected(aux);
    }    
  }
  console.log(selected)

  if(products.length === 0){
    console.log('loading...');
  } else {
    return (
      <Main>
        <header>
          <img src={userImg} alt="user"></img>
          <h1>iFome</h1>
          <img src={shoppingImg} alt="shpping"></img>
        </header>
        <div className="shopping"></div>
        <div>
          {products.map((el) => {
            return(
              <div key={el._id} className="category">
                <h2>{el.type}</h2>
                {el.products.map((product) => {
                  return(
                    <div key={product.id} className="products" onClick={() => selectedIten(product.id)}>
                      <img src={product.image} alt={product.name}></img>
                      <img className="confirm" src={confirm} alt="x"></img>
                      <h3>{product.name}</h3>
                      <p>{product.description}</p>
                    </div>
                  )
                })}
              </div>
            )
          })}
        </div>
      </Main>
    )
  }
}

const Main = styled.main`
  header {
    background-color: var(--theme);
    display: flex;
    justify-content: space-between;
    align-items: center;
  }
  h1 {
    font-family: "Saira Stencil One", cursive;
    font-size: 32px;
    color: #ffffff;
  }
  img {
    height: 40px;
    color: white;
  }
  .shopping {
  }
  .category {
    background-color: antiquewhite;
    margin-top: 20px;
    height: 180px;
    display: flex;
    position: relative;
  }
  h2 {
    position: absolute;
    top: 10px;
  }
  .products {
    cursor: pointer;
    position: relative;
  }
  .products img {
    margin-top: 40px;
    margin-inline: 5px;
    height: 100px;
    width: 160px;
    border-radius: 10px;
  }
  .confirm {
    height: 25px !important;
    width: 25px !important;
    position: absolute:  !important;;
    z-index: 1;
  }
`
